import React, { useState, useRef } from "react"
import { Link } from "react-router-dom"
import Menu from "./Menu"
import hamburger from "../icons/hamburger.png"
import closeMenu from "../icons/close-menu.png"

export default function Navbar() {
    const [showMenu, setShowMenu] = useState(false)
    const [source, setSource] = useState(hamburger)
    const paths = ["/", "/about", "/products", "/contact", "/cart" ]

    // alters string in paths array to get capitalized path name for nav bar link
    const getPathName = (path) => {
        const newString = path.slice(1)
        const capitalizedNewPath = newString.charAt(0).toUpperCase() + newString.slice(1)
        return capitalizedNewPath
    }

    console.log(imageRef.src);

    const navLinkElements = paths.map(path => {
        return (
                <Link
                    key={path}
                    to={path}
                    className="nav-link"               >
                    {path === "/" ? "Home" : getPathName(path)}
                </Link>
        )
    })

    return (
        <nav>
            <Menu show={showMenu} />
            <Link to={"/"}>
                <p className="nav-logo">logo</p>
            </Link>

            <div className="nav-links-container">
                {navLinkElements}
            </div>

            <img className="nav-button" 
                src={source} 
                alt="menu icon"
                onClick={()=> {
                    setShowMenu(prevShowMenu => !prevShowMenu)
                    imageRef.src === hamburger ? imageRef.src = closeMenu : imageRef.src = hamburger
                }}
            />
        </nav>
    )
}
