import { productsData } from "../data.js/productsData"
import Favorite from "../Components/Favorite"
import craft from "../images/craft.jpg"


export default function Home() {
    
    const favoriteProducts = productsData.filter(product => product.isFavorite)
    const favoriteElements = favoriteProducts.map(product => {
      const {id, image, title, description } = product
      return (
          <Favorite
            key={id} 
            id={id}
            image={image}
            title={title}
            description={description}
          />
        ) 
    })
    
    return (
        <div>
            <h3 className="app-banner">Shop our favorites!</h3>

            <main>
              <div className="main-favorite-cards">
                  {favoriteElements}
              </div>
    
              <div className="main-about">
                <div className="main-about-flex-container">
                  <h3 className="main-about-subheading">About</h3>
                  <img className="main-about-image"src={craft} alt="yarn layed out on a desk" />
                  <p className="main-about-text">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor 
                  incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis 
                  nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. 
                  Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu 
                  fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in 
                  culpa qui officia deserunt mollit anim id est laborum.</p>
                </div>
              </div>
            </main>
        </div>
    )
}
