import React, { useState } from "react"
// import { Link } from "react-router-dom"
import Menu from "./Menu"
import hamburger from "../icons/hamburger.png"
import closeIcon from "../icons/close-menu.png"

export default function Navbar() {
    const [showMenu, setShowMenu] = useState(false)
    const paths = ["/", "/about", "/products", "/contact", "/cart" ]

    // alters string in paths array to get capitalized path name for navigation links
    const getPathName = (path) => {
        const newString = path.slice(1)
        const capitalizedNewPath = newString.charAt(0).toUpperCase() + newString.slice(1)
        return capitalizedNewPath
    }

    const navLinkElements = paths.map(path => {
        let isCurrentPath = path === window.location.pathname
        console.log(isCurrentPath);

        return (
                // <Link
                //     key={path}
                //     to={path}
                // >
                    <li className="nav-link">
                        <a href={path} style={isCurrentPath ? {fontWeight: "bold"} : ""}>
                            {path === "/" ? "Home" : getPathName(path)}
                        </a>
                    </li>
                // </Link>
        )
    })

    return (
        <nav>
            <Menu show={showMenu} />

            <a href="/"><p className="nav-logo">logo</p></a>
            

            <ul className="nav-links-container">
                {navLinkElements}
            </ul>

            <img className="nav-button" 
                src={showMenu ? closeIcon : hamburger}
                alt="menu icon"
                onClick={()=> {
                    setShowMenu(prevShowMenu => !prevShowMenu)
                }}
            />
        </nav>
    )
}
