import React from "react"
import { useDispatch } from "react-redux"
import { addToCart, removeFromCart } from "../../Redux/Actions/cartActions"

export function CartItem(props) {
    const dispatch = useDispatch()

    const removeFromCartHandle = id => {
        dispatch(removeFromCart(id))
    }

    const addToCartHandle = id => {
        dispatch(addToCart(id, props.qty))
    }

    const calculateItemPrice = () => {
        return (props.qty * props.price) / 100
    }
    return (
        <div className="cart-item">
            <img className="cart-item-image" src={props.image} alt={props.name} />
            <h3 className="cart-item-title">{props.name}</h3>            
            <div className="cart-item-quantity flex">
                <p>quantity: {props.qty}</p>
            
                <div className="quanitity-counter flex">
                    <button className="increase-button" onClick={() => addToCartHandle(props.id)}>+</button>
                    <p>{props.qty}</p>
                    <button className="decrease-button" onClick={() => addToCartHandle(props.id)}>-</button>
                </div>

                <button 
                    className="cart-item-remove-button"
                    onClick={() => removeFromCartHandle(props.id)}
                    >Remove from cart</button>
            </div>
        
            <p className="cart-item-price">Price: ${calculateItemPrice()}</p>
        </div>      
                 
    )
        
}
