import React, { useEffect, useContext } from "react"
import { useParams } from "react-router-dom"
import { useDispatch, useSelector } from 'react-redux';
import { addToCart } from "../Redux/Actions/cartActions.js"
// import { CartContext } from "../Context/CartContext"
import { CartItem } from "../Components/cartComponents/CartItem"
import Navbar from "../Components/Navbar"

export default function Cart() {
    const { location } = window
    const { productId } = useParams()
    const qty = location.search ? Number(location.search.split("=")[1]) : 1
    const dispatch = useDispatch()

    const cart = useSelector(state => state.cart)
    const { cartItems } = cart

    // useEffect(() => {
    //     if (productId) {
    //         dispatch(addToCart(productId, qty))
    //     }
    // }, [dispatch, productId, qty])
    // const { emptyCart, cartItems } = useContext(CartContext)
    // const isCartEmpty = cartItems.length === 0
    if(cartItems.length === 0) {
        return (
            <>
                <Navbar />
                <div className="cart-container">
                    <h1>Cart is empty</h1>
                </div>  
            </>
        )
    }
    const cartItemElements = cartItems.map(item => {
        return (
            <CartItem 
                key={item.product}
                image={item.image} 
                name={item.name}
                price={item.price}
                qty={item.qty}
            />
        )
    })

    // function calculateTotalPrice() {
    //     return cartItems.reduce((accumulator, value) => {
    //         return accumulator + (value.quantity * value.price)
    //     }, 0)
    // }

    return (
        <>
            <Navbar />
            <div className="cart-container flex">
                <h2 className="cart-heading">Cart</h2>
                <p className="cart-price-heading">Price</p>
                {cartItemElements}
                <button className="empty-cart-button" onClick={() => localStorage.clear()}>Empty Cart</button>
            </div>
        </>
    )
}
