import React, { useEffect, useContext } from "react"
import { useParams } from "react-router-dom"
import { useDispatch, useSelector } from 'react-redux';
import { emptyCart } from "../Redux/Actions/cartActions.js"
// import { CartContext } from "../Context/CartContext"
import { CartItem } from "../Components/cartComponents/CartItem"
import Navbar from "../Components/Navbar"

export default function Cart() {
    // const { location } = window
    // const { productId } = useParams()
    // const qty = location.search ? Number(location.search.split("=")[1]) : 1
    const dispatch = useDispatch()
    const cart = useSelector(state => state.cart)
    const { cartItems } = cart

    const emptyCartHandle = e => {
        e.preventDefault()
        dispatch(emptyCart())
    }

    if(cartItems.length === 0) {
        return (
            <>
                <Navbar />
                <div className="cart-container">
                    <h1>Cart is empty</h1>
                </div>  
            </>
        )
    }
    const cartItemElements = cartItems.map(item => {
        return (
            <CartItem 
                key={item.product}
                id={item.product}
                image={item.image} 
                name={item.name}
                price={item.price}
                qty={item.qty}
            />
        )
    })

    function calculateTotalPrice() {
        return cartItems.reduce((price, product) => {
            return (price + (product.qty * product.price)) / 100
        }, 0)
    }

    return (
        <>
            <Navbar />
            <div className="cart-container flex">
                <h2 className="cart-heading">Cart</h2>
                {cartItemElements}
                <p className="cart-total-price">Total: ${calculateTotalPrice(cartItems)}</p>
                <button className="empty-cart-button" onClick={(e) => emptyCartHandle(e)}>Empty Cart</button>
            </div>
        </>
    )
}
