import React from "react"
import { Link } from "react-router-dom"
import { useSelector } from 'react-redux';
import { CartItem } from "../Components/cartComponents/CartItem"
import Navbar from "../Components/Navbar"

export default function Cart() {
    
    const cart = useSelector(state => state.cart)
    const {cartItems} = cart
    const isCartEmpty = cartItems.length === 0

    const cartItemElements = cartItems.map(item => {
        return (
            <CartItem 
                key={item.product}
                id={item.product}
                image={item.image} 
                name={item.name}
                price={item.price}
            />
        )
    })

    const emptyCartElements = (
        <>
            <h2>Your cart is empty.</h2>
            <Link to="/products" className="empty-cart-link">Start Shopping!</Link>
        </>
    )

    function calculateTotalPrice() {
        return cartItems.reduce((price, product) => {
            return (Number(price) + product.price * product.qty / 100).toFixed(2)
        }, 0)
    }

    return (
        <>
            <Navbar />
            <div className="cart-container flex">
                <div className="cart-header">
                    {isCartEmpty 
                        ? emptyCartElements
                        : <h2>Items In Cart ({cartItems.length})</h2>
                    }
                </div>

                {cartItemElements}

                <p className="cart-total-price">Total: ${calculateTotalPrice()}</p>

                <div className="cart-links flex">
                    <Link to="/products" className="cart-shopping-link">CONTINUE SHOPPING</Link>
                    <Link to="/checkout" className="cart-checkout-link">CHECKOUT</Link>
                </div>
            </div>
        </>
    )
}
