import React, { useState } from "react"

const CartContext = React.createContext()

function CartContextProvider(props) {
    const [cartItems, setCartItems] = useState([])

    function addToCart(item) {
        setCartItems(prevCart => {
            // check if cart already has item
            if(!prevCart.includes(item)) {
                item.quantity = 1
                return [...prevCart, item]
            } else {
                console.log("Already in cart");
                return prevCart
            }
        })
    }

    function removeCartItem(product) {
        setCartItems(prevCart => prevCart.filter(item => item !== product))
    }

    function emptyCart() {
        setCartItems([])
    }

    function updateQuantity(e, product) {
        if(e.target.innerText === "+") {
            setCartItems(cartItems.map(prevItem => {
                if(product === prevItem) {
                    return {...prevItem, quantity: prevItem.quantity + 1}
                } else {
                    return {...prevItem}
                }


            }))
        }
    }

    return (
        <CartContext.Provider 
            value={{
                cartItems,
                updateQuantity,
                addToCart,
                removeCartItem,
                emptyCart
            }}>
            {props.children}
        </CartContext.Provider>
    )
}

export {CartContextProvider, CartContext}
