import React, { useState } from "react"
import Navbar from "../Components/Navbar"

const Login = () => {
    const [loginForm, setLoginForm] = useState({
        email: "",
        password: "",
    })

    const handleChange = e => {
        const {name, value} = e.target
        setLoginForm(prevForm => {
            return {
                ...prevForm,
                [name]: value
            }
        })
    }

    console.log(loginForm);
    return (
        <>
            <Navbar />
            <form>
                <input type="email" name="email" value={loginForm.email} onChange={(e) => handleChange(e)} />
                <input type="password" name="password" value={loginForm.password} onChange={(e) => handleChange(e)} />
                <button>LOGIN</button>
                <p>Create Account</p>
            </form>
        </>
    )
}

export default Login
