import React  from "react"
import hamburger from "../icons/hamburger.png"

export default function Navbar(props) {
    const paths = ["Home", "About", "Products", "Contact", "Cart" ]
    const currentPath = window.location.pathname.slice(1)
    console.log(currentPath);

    const navLinkElements = paths.map(path => {
        return (
                <a
                key={path}
                href={path === "Home" ? "/" : `/${path.toLowerCase()}`} // uses "/" for the href instead of "home"
                className={`nav-link ${path.toLowerCase() === currentPath ? "active" : ""}`}
                >
                {path}
                </a>
        )
    })

    return (
        <div className="App-header-nav">
                <p>logo</p>
                <div className="nav-links-container">
                    {navLinkElements}
                </div>
                <img className="nav-hamburger" 
                    src={hamburger} 
                    alt="menu icon" 
                    onClick={props.handleClick}
                />
            </div>
    )
}
