import React, { useEffect, useState } from "react"
import { Link, useParams } from "react-router-dom"
// import { CartContext } from "../Context/CartContext"
// import { products } from "../data/products"
import Navbar from "../Components/Navbar"
import { useDispatch, useSelector } from 'react-redux';
import { listSingleProduct } from "../Redux/Actions/ProductActions.js"

/**
 * @component
 * @ProductDetails
 * 
 * @returns 
 */
export default function ProductDetails({ history }) {
    // const { addToCart, cartItems } = useContext(CartContext)
    // const [product, setProduct] = useState([])
    const [qty, setQty] = useState(1)
    const { productId } = useParams()
    const dispatch = useDispatch()

    const productDetails = useSelector(state => state.productDetails)
    const { loading, error, product } = productDetails

    useEffect(() => {
        dispatch(listSingleProduct(productId))
    }, [dispatch, productId])

    const addToCartHandle = (e) => {
        e.preventDefault()
        history.pushState(`/cart/${productId}?qty=${qty}`)
    }

    // useEffect(()=>{
    //     const fetchProducts = async() => {
    //         const response = await fetch(`/api/products/${productID}`)
    //         const product = await response.json()
    //         setProduct(product)
    //     }
    //     fetchProducts()
    // }, [productID])
    
    const { title, image, description, price } = product
    // const isInCart = cartItems.some(item => item.id === product.id)

    return (
        <>
            <Navbar />
            <div className="product-container flex">
                <h2 className="product-title">{title}</h2>
                <img className="product-image" src={image} alt={title}/>
                <p className="product-description">{description}</p>
                <p className="product-price bold">${price / 100}</p>

                <div>
                    <button
                        className={`add-to-cart-button`}
                        // className={`add-to-cart-button ${isInCart ? "disabled-button" : ""}`}
                        onClick={()=> addToCart(product)}
                        disabled={isInCart}
                    >Add to cart</button>

                    {isInCart ? <span>added to cart!</span> : ""}
                </div>
                {isInCart ? <Link to="/cart" className="cart-link">Go to cart</Link> : ""}
            </div>
        </>
    )
}
