import React from "react"

export function CartItem(props) {
    return (
        <div className="cart-item-container">
            <div className="cart-item">
                <img src={props.img} alt={props.title} />
                <h3>{props.title}</h3>
                <p>{props.description}</p>
            </div>

            <button>Remove from cart</button>
        </div>
    )
        
}
