import Header from "./Components/Header"
import Menu from "./Components/Menu"
import craft from "./images/craft.jpg"
import {useState} from "react"
// import './Menu.css';
import './App.css';

function App() {
  const [isMenuShown, setIsMenuShown] = useState(false)
  return (
    <div className="App-container">
      <Header handleClick={() => setIsMenuShown(!isMenuShown)}/>
      {<Menu show={isMenuShown}/>}

      <main>
        <h3 className="main-subheading">About</h3>
        <div className="main-about">
          <img className="about-image"src={craft} alt="yarn layed out on a desk" />
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor 
            incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis 
            nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. 
            Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu 
            fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in 
            culpa qui officia deserunt mollit anim id est laborum.</p>
        </div>
      </main>
    </div>
  );
}

export default App;
