import React from "react"
import { Link } from "react-router-dom"
import { useDispatch, useSelector } from 'react-redux';
import {  emptyCart } from "../Redux/Actions/cartActions.js"
import { CartItem } from "../Components/cartComponents/CartItem"
import Navbar from "../Components/Navbar"

export default function Cart() {
    const dispatch = useDispatch()
    const cart = useSelector(state => state.cart)
    const {cartItems} = cart
    console.log(cartItems);
    const emptyCartHandle = e => {
        e.preventDefault()
        dispatch(emptyCart())
    }

    if(cartItems.length === 0) {
        return (
            <>
                <Navbar />
                <div className="cart-container">
                    <div className="empty-cart-cta">
                        <h2>Your cart is empty.</h2>
                        <Link to="/products" className="empty-cart-link">Start Shopping!</Link>
                    </div>
                </div>  
            </>
        )
    }
    const cartItemElements = cartItems.map(item => {
        return (
            <CartItem 
                key={item.product}
                id={item.product}
                image={item.image} 
                name={item.name}
                price={item.price}
            />
        )
    })

    function calculateTotalPrice() {
        return cartItems.reduce((price, product) => {
            return (Number(price) + product.price * product.qty / 100).toFixed(2)
        }, 0)
    }

    return (
        <>
            <Navbar />
            <div className="cart-container flex">
                <h2 className="cart-heading">Cart</h2>
                {cartItemElements}
                <p className="cart-total-price">Total: ${calculateTotalPrice()}</p>
                <button className="empty-cart-button" onClick={(e) => emptyCartHandle(e)}>Empty Cart</button>
                <Link to="/products">CONTINuE SHOPPING</Link>
            </div>
        </>
    )
}
